var mssql = global.get('mssql');

var procedure_name = '[Recado].[Novo]'

var request = new mssql.Request(global.get('ERP'));

request.input('id', mssql.VarChar(36), msg.payload.document.id)
request.input('produto', mssql.VarChar(50), msg.payload.document.produto)
request.input('cliente', mssql.VarChar(50), msg.payload.document.cliente)
request.input('empresa', mssql.VarChar(50), msg.payload.document.empresa)
request.input('endereco', mssql.VarChar(50), msg.payload.document.endereco)
request.input('numero', mssql.VarChar(10), msg.payload.document.numero)
request.input('complemento', mssql.VarChar(50), msg.payload.document.complemento)
request.input('contato', mssql.VarChar(50), msg.payload.document.contato)
request.input('ddd', mssql.VarChar(2), msg.payload.document.ddd)
request.input('telefone', mssql.VarChar(10), msg.payload.document.telefone)
request.input('representante', mssql.VarChar(50), msg.payload.document.representante.codigo)
request.input('regiao', mssql.VarChar(50), msg.payload.document.regiao)

request.execute(procedure_name).then(function(recordsets, returnValue, affected) {

    if (Array.isArray(recordsets) && recordsets.length > 0) {

        if (Array.isArray(recordsets[0]) && recordsets[0].length > 0 && recordsets[0][0].numero === 0) {

            node.status({ fill:"green", shape:"dot", text: 'Recado incluido ok.' });

            node.send({req: msg.req, res: msg.res, payload: recordsets[1][0]});
        } else {
            node.status({ fill:"red", shape:"dot", text: recordsets[0][0].mensagem || 'Erro desconhecido' });

            node.send({statusCode: 500, req: msg.req, res: msg.res, payload: recordsets[0][0]});
        }

    } else {
        node.status({ fill:"red", shape:"dot", text: recordsets[0][0].mensagem || 'Erro desconhecido' });

        return {statusCode: 500, req: msg.req, res: msg.res, payload: {number: -1, message: 'Internal server error: invalid response from database.'}};
    }

})
.catch(function(err) {
    console.log('-----------------> Erro ao chamar Stored Procedure ' + procedure_name + ' <-----------------')
    console.log(JSON.stringify(err !== null && typeof err === 'object' && err))
    msg.payload = err !== null && typeof err === 'object' ? err : { erro: 9999, mensagem: err || 'Erro desconhecido.' };
    node.status({ fill:"red", shape:"dot", text: err !== null && typeof err === 'object' ? err.message : err });
    node.send(msg);
});

